targets.expr:
sum(
  node_namespace_pod_container:container_cpu_usage_seconds_total:sum_irate{exported_namespace=~"$api_policy_namespace"}
* on(namespace, exported_namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{exported_namespace=~"$api_policy_namespace", workload_type="deployment"}
) by (workload, workload_type)
* on(workload) (group by(workload) (label_replace(gatewayapi_httproute_labels{exported_namespace=~"$api_policy_namespace",name=~"$route_name"}, "workload", "$1","deployment", "(.+)")))

sum(
    container_memory_working_set_bytes{metrics_path="/metrics/cadvisor", exported_namespace=~"$api_policy_namespace", container!="", image!=""}
  * on(namespace, exported_namespace,pod)
    group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{exported_namespace=~"$api_policy_namespace", workload_type="deployment"}
) by (workload, workload_type)
* on(workload) (group by(workload) (label_replace(gatewayapi_httproute_labels{exported_namespace=~"$api_policy_namespace",name=~"$route_name"}, "workload", "$1","deployment", "(.+)")))
